function commentsToggle(){var e=document.getElementById("comments__inner-container"),n=document.getElementById("comments-toggle"),t=document.getElementById("comments__title-toggle");n.onclick=function(){"comments__inner-container"===e.className?(e.className+=" comments__inner-container--toggled",t.style.transform="rotateX(180deg)"):(e.className="comments__inner-container",t.style.transform="rotateX(0deg)")}}window.onload=function(){commentsToggle()};